import React from 'react';
import {Image, Text, TouchableOpacity, View} from 'react-native';
import {useNavigation} from "@react-navigation/native";
import styles from "./styles"
import {useAuth} from "../../contexts/Auth";
import {useInteractions} from "../../contexts/UserInteractions";
import {LOREM} from "../../services/constantes";

// PROPS
export type artworkProps = {
    id: string;
    imageUri: string;
    artistName: string;
    artworkName: string;
    artistId: string,
}

/**
 * Component displaying an Artwork with image, name and artist name
 *
 * <img src="https://drive.google.com/uc?export=view&id=19vamBJAY1JchpK47QNtNfrkuGSZwz120" alt="AllMyArtistsMockup" height="300">
 * @param {artworkProps} props
 * @param {string} props.id - ID of the artwork
 * @param {string} props.imageUri - URI of the image
 * @param {string} props.artistName - Name of the Artist
 * @param {string} props.artworkName - Name of the artwork
 * @param {string} props.artistId - ID of the artist
 * @return ArtworkComponent
 */
export function ArtworkComponent(props: artworkProps) {

    const navigation = useNavigation();

    const auth = useAuth()

    const userInt = useInteractions()

    // WHEN CLICKED NAVIGATES TO ARTWORK PAGE
    const onPressArtwork = () => {
        navigation.navigate('ArtworkPage', {
            id: props.id,
            imageUri: props.imageUri,
            information: LOREM,
            artworkName: props.artworkName,
            artistId: props.artistId,
            artistName: props.artistName
        })
    }

    // FUNCTION THAT FILTERS THROUGH AUTH.SELECTED TO FIND THE ACCORDING ID AND THEN REMOVES IT IF ITS PRESENT AND ADDS IT OTHERWISE
    const addToSelected = () => {
        // IF ALREADY IN LIST THEN REMOVE
        if (userInt.selected.includes(props.id))
            userInt.setSelected(userInt.selected.filter(el => el !== props.id))
        // ELSE ADD IT TO LIST
        else
            userInt.setSelected([...userInt.selected, props.id])
    }

    return (
        <View style={styles.container}>
            {/*CUSTOM BUTTON - ON PRESS NAVIGATE TOWARDS ARTWORK PAGE*/}
            <TouchableOpacity onPress={() => userInt.edit ? addToSelected() : onPressArtwork()}>
                <Image source={{uri: props.imageUri}} style={styles.image}/>
                {/*ARTWORK NAME - TEXT*/}
                <Text numberOfLines={1} style={styles.artworkName}>{props.artworkName}</Text>
                {/*ARTIST NAME - TEXT*/}
                <Text numberOfLines={1} style={styles.artistName}>{props.artistName}</Text>
            </TouchableOpacity>
        </View>
    )
}

import React, {useEffect, useState} from 'react';
import axios from "axios";
import {URL} from "../../../services/constantes";
import ArtworkCarouselDouble from "../index";
import {Artwork} from "../../../../types";

// PROPS
export type PaintingsMatchedFromSourceProps = {
    paintingId: string,
    matchingType: string,
}

/**
 * Component displaying an {@link ArtworkCarouselDouble} of matched artworks
 *
 * <img src="https://drive.google.com/uc?export=view&id=1P-OOAmbxkpiDfb9I9mGkj2NMLePBXLaf" alt="ArtworkPage" height="500">
 * @param {PaintingsMatchedFromSourceProps} props
 * @param {string} props.paintingId - ID of the artwork
 * @param {string} props.matchingType - Type of matching (Color, Genre, Origin, Style, ...)
 * @return ArtworkCarouselDouble
 * */
export default function PaintingsMatchedFromSource(props: PaintingsMatchedFromSourceProps) {

    const [matchList, setMatchList] = useState<any>([]);

    //API CALL TO GET AW_IDTF_INTERN
    useEffect(() => {
        axios.get(URL + `paintingEntity/${props.paintingId}`).then((response) => {

            //API CALL TO GET MATCHED PAINTINGS FROM AW_IDTF_INTERN
            axios.get(URL + `matchingSearch/findMatchForSource/${props.matchingType}/${response.data.aw_idtf_intern}/true`).then((response2) => {

                //PARSING DATA
                const values : Artwork[] = Object.values(response2.data).map((el : any) => {
                    return {
                        id: el.id,
                        imageUri: URL + `images/content/mini/${el.artistId}/${el.urlImage}`,
                        artistName: el.artisteName,
                        artworkName: el.name,
                        artistId: el.artistId,
                    }
                })

                setMatchList(values)
            });
        });
    }, [props.paintingId]);

    return (<ArtworkCarouselDouble title={matchList.length > 0 ? "Matched" : "No matched paintings for this artwork"} data={matchList}/>)
}

import React, {useEffect, useState} from 'react';
import axios from "axios";
import ArtworkCarouselDouble from "../index";
import {URL} from "../../../services/constantes";
import {Artwork} from "../../../../types";
import {useNavigation} from "@react-navigation/native";

/**
 * Component displaying an {@link ArtworkCarouselDouble} of the most matched artworks for the last 30 days
 *
 * //TODO IMAGE
 * @constructor
 * @return ArtworkCarouselDouble
 */
export default function MostMatchedComponent() {

    const [matchedPainting, setMatchedPainting] = useState<[Artwork] | []>([]);

    const navigation = useNavigation()

    //API CALL TO GET LATEST MOST MATCHED ARTWORKS
    useEffect(() => {
        axios
            .get(URL + "matchingSearch/moreMatchedPaintingForLast30Days?numberOfMonthsBefore=1", {
                headers: {
                    "Access-Control-Allow-Origin": "*",
                    "Content-Type": "application/json"
                }
            })
            .then((response) => {

                //PARSING DATA
                const parsedData = response.data.map((el: any) => {
                    return {
                        id: el.painting.id,
                        imageUri: URL + `images/content/mini/${el.painting.artistId}/${el.painting.urlImage}`,
                        artistName: el.painting.artisteName,
                        artworkName: el.painting.name,
                        artistId: el.painting.artistId
                    }
                })

                setMatchedPainting(parsedData)
            });
    }, []);

    return (
        <ArtworkCarouselDouble title={"Most matched"} data={matchedPainting} navigate={() => navigation.navigate("ListContentPage", {id: undefined, name: "Most matched", data: matchedPainting})}/>
    )
}

import React, {useEffect, useState} from 'react';
import axios from "axios";
import {LOREM, URL} from "../../../services/constantes";
import ArtworkCarouselDouble from "../index";
import {Artwork} from "../../../../types";
import {useNavigation} from "@react-navigation/native";

//PROPS
export type PaintingsFromSameArtistProps = {
    artistId: string
}

/**
 * Component displaying an {@link ArtworkCarouselDouble} of artworks from the same artist as artistId
 *
 * <img src="https://drive.google.com/uc?export=view&id=1OmJnk8nQRJG-nkCNVO9paEYwhM0Wd64d" alt="ArtworkPage" height="300">
 * @param {PaintingsFromSameArtistProps} props
 * @param {string} props.artistId - ID of the artist
 * @constructor
 * @return ArtworkCarouselDouble
 */
export function PaintingsFromSameArtistComponent(props: PaintingsFromSameArtistProps) {

    const navigation = useNavigation()

    const [fromArtist, setFromArtist] = useState<[Artwork] | []>([]);

    // API CALL TO GET ARTWORKS FROM SAME ARTIST
    useEffect(() => {
        axios.get(URL + `paintingEntity/byArtist/${props.artistId}/0`).then((response) => {
            const data = response.data.map((el: any) => {
                return {
                    id: el.id,
                    imageUri: URL + `images/content/mini/${el.artistId}/${el.urlImage}`,
                    artistName: el.artisteName,
                    artworkName: el.name,
                    artistId: el.artistId
                }
            // only need 10
            }).slice(0,10)

            setFromArtist(data)
        });
    }, [props.artistId]);

    // ON CLICKED, NAVIGATES TO ARTIST PAGE
    const onPressArtist = () => {
        if (fromArtist[0] != undefined) {
            navigation.navigate('ArtistNonUserPage', {
                imageUri: fromArtist[0].imageUri,
                information: LOREM,
                artistName: fromArtist[0].artistName,
                artistId: props.artistId,
            })
        }
    }

    return (
            <ArtworkCarouselDouble
                title={fromArtist[0] != undefined ? `Also from ${fromArtist[0].artistName}` : "No other works from this artist"}
                data={fromArtist} navigate={() => onPressArtist()}/>
    )
}


/**
 * USED FOR CLEAN DOC AND FUNCTIONAL CODE
 * @ignore
 */
const PaintingsFromSameArtist = React.memo(PaintingsFromSameArtistComponent)
export { PaintingsFromSameArtist }

import React from 'react';
import {FlatList, SafeAreaView, ScrollView, Text, TouchableOpacity, View} from 'react-native';
import {Artwork, artworkCarouselDoubleProps} from "../../../types";
import styles from "./styles";
import {Entypo} from "@expo/vector-icons";
import {ArtworkComponent} from "../Artwork";

/**
 * Component rendering a horizontally scrollable double carousel of artworks
 * @param {artworkCarouselDoubleProps} props
 * @param {string} props.title - Name of the Carousel
 * @param {Artwork[] | undefined} props.data - Array of artworks
 * @param {void} props.navigated - Navigation destination
 * @constructor
 * @return ArtworkCarouselDouble
 */
export default function ArtworkCarouselDouble(props: artworkCarouselDoubleProps) {

    //RENDER ITEM
    const renderItem = ({item, route}: any): JSX.Element => {
        return (<ArtworkComponent id={item.id} imageUri={item.imageUri}
                                  artistName={item.artistName}
                                  artworkName={item.artworkName} artistId={item.artistId}/>)
    }


    if (props.data == undefined) return (<View></View>)


    return (<SafeAreaView>
        {/*TITLE OF ARTWORK DOUBLE CAROUSEL*/}
        {/*IF CAROUSEL HAS LINK*/}
        {props.navigate != undefined ?
            //@ts-ignore
            <TouchableOpacity onPress={() => props.navigate()}>
                <View style={styles.textAndChevron}>
                    {/*TEXT*/}
                    <Text style={styles.title}>{props.title}</Text>
                    {/*CHEVRON*/}
                    <Entypo name={"chevron-right"} size={30} color={"white"}/>
                </View>

            </TouchableOpacity> :

            //IF CAROUSEL HAS NO LINK THEN SIMPLE TITLE
            <Text style={styles.title}>{props.title}</Text>}

        <ScrollView horizontal showsVerticalScrollIndicator={false} showsHorizontalScrollIndicator={false}
                    contentContainerStyle={{paddingVertical: 7}}>

            <FlatList
                scrollEnabled={false}
                initialNumToRender={3}
                numColumns={props.data.length}
                showsVerticalScrollIndicator={false}
                showsHorizontalScrollIndicator={false}
                keyExtractor={(item) => item.id}
                key={props.data.length}
                data={props.data}
                extraData={props.data}
                renderItem={renderItem}/>
        </ScrollView>
    </SafeAreaView>)
}

import React, {useEffect, useState} from 'react';
import {Text, TouchableOpacity, View} from 'react-native';
import {Artwork} from "../../../../types";
import ArtworkCarouselSingle from "../index";
import styles from "./styles";
import {useNavigation} from "@react-navigation/native";
import axios from "axios";
import {URL} from "../../../services/constantes";
import {Entypo} from "@expo/vector-icons";
import ColorPickerModal from "../../Modals/ColorPickerModal";

/**
 * Function used to generate a random color when mounting the component for the first time
 * @return : string of random HEX color with "#" in front*/
const generateColor = () => {
    const randomColor = Math.floor(Math.random() * 16777215)
        .toString(16)
        .padStart(6, '0');
    return `#${randomColor}`;
};

/**
 * Color generated randomly on first mount
 * @type string
 */
export const randomColor = generateColor()

/**
 * Component displaying a {@link ArtworkCarouselSingle} of similar color artworks and a button letting the user choose the color he wants
 *
 * <img src="https://drive.google.com/uc?export=view&id=1Sq3S7MHro-EJdp4Zor4zsuowkmH3vaOb" alt="ArtworkPage" height="200">
 * @constructor
 * @return DiscoverByColor
 */
export default function DiscoverByColor() {

    const [paintingByColor, setPaintingByColor] = useState<[Artwork] | []>([]);
    const [isModalVisible, setModalVisible] = useState(false);
    const [color, setColor] = useState(randomColor)
    const navigation = useNavigation()

    // API CALL TO GET PAINTINGS OF THE SAME COLOR
    useEffect(() => {
        axios
            .post(URL + `paintingSearch/findByColor`, {
                colorCode: color.slice(1), sortOrder: "ASC"
            }, {headers: {"Access-Control-Allow-Origin": "*", "Content-Type": "application/json"}})
            .then((response) => {

                // PARSING DATA
                const parsedData = response.data.map((el: any) => {
                    return {
                        id: el.id,
                        imageUri: URL + `images/content/mini/${el.artistId}/${el.urlImage}`,
                        artistName: el.artisteName,
                        artworkName: el.name,
                        artistId: el.artistId
                    }
                })

                setPaintingByColor(parsedData)
            });
    }, [color, isModalVisible]);

    const toggleModal = () => {
        setModalVisible(!isModalVisible);
    };

    return (
        <View>
            {/*MODAL FOR COLOR PICKER*/}
            <ColorPickerModal isModalVisible={isModalVisible} setModalVisible={setModalVisible} color={color}
                              setColor={setColor} data={paintingByColor}/>

            <View style={styles.container}>

                {/*IF CLICK ON TITLE THEN NAVIGATE TO PAGE FULL OF ARTWORKS*/}
                <TouchableOpacity onPress={() => navigation.navigate("ListContentPage", {
                    id: undefined,
                    name: color,
                    data: paintingByColor
                })}>
                    <View style={styles.textAndChevron}>
                        {/*TEXT*/}
                        <Text style={styles.title}>Discover by color</Text>
                        {/*CHEVRON*/}
                        <Entypo name={"chevron-right"} size={30} color={"white"}/>
                    </View>
                </TouchableOpacity>

                {/*CHOOSE COLOR BUTTON*/}
                <TouchableOpacity style={[styles.button, {backgroundColor: color}]} onPress={() => toggleModal()}>
                    <Text style={styles.buttonText}>Choose colour</Text>
                </TouchableOpacity>

                {/*CAROUSEL OF RANDOM COLOR ARTWORKS*/}
                <ArtworkCarouselSingle data={paintingByColor}/>
            </View>
        </View>)
}

import React, {useEffect, useState} from 'react';
import axios from "axios";
