/**
 * Component displaying an {@link ArtworkCarouselSingle} of liked artworks
 *
 * <img src="https://drive.google.com/uc?export=view&id=17_yTpjYuS72FhFQ6SZGlj8Opw5MBNRSe" alt="ArtworkPage" height="200">
 * @constructor
 * @param {string} props.id - ID of the user
 * @return LikedArtworksCarousel
 */
const LikedArtworksCarousel = (props : LikedArtworksCarouselProps) => {

    const navigation = useNavigation()
    const auth = useAuth()

    const [painting, setPainting] = useState<any>([]);

    if (!props.id)
    // API CALL TO GET ALL LIKED ARTWORKS OF USER AND PARSE THEM INTO `ARTWORK`
    {
        useEffect(() => {
            setPainting([])
            auth.authData.paintingLiked && auth.authData.paintingLiked.forEach((paintingId) => {
                axios.get(URL + `paintingEntity/${paintingId}`).then((response) => {
                    let data = response.data;
                    let parsedData = {
                        id: data.id,
                        imageUri: URL + `images/content/mini/${data.artist_idtf_intern}/${data.generatedId}`,
                        artistName: data.artist_name_tot,
                        artworkName: data.aw_title_Eng,
                        artistId: data.artist_idtf_intern
                    }
                    setPainting((painting: any) => [...painting, parsedData])
                });
            })
        }, [auth.authData.paintingLiked, auth.refreshKey])
    }
    else
    {
        useEffect(() => {
            setPainting([])
            axios.get(URL + `users/${props.id}`).then((response) => {
                response.data.paintingLiked.forEach((paintingId: string) => {
                    axios.get(URL + `paintingEntity/${paintingId}`).then((response) => {
                        let data = response.data;
                        let parsedData = {
                            id: data.id,
                            imageUri: URL + `images/content/mini/${data.artist_idtf_intern}/${data.generatedId}`,
                            artistName: data.artist_name_tot,
                            artworkName: data.aw_title_Eng,
                            artistId: data.artist_idtf_intern
                        }
                        setPainting((painting: any) => [...painting, parsedData])
                    });
                })
            })
        }, [props.id])
    }
    // useEffect(() => {
    //     setPainting([])
    //     if (auth.authData.paintingLiked) {
    //         for (let i = 0; i < auth.authData.paintingLiked.length; i++)
    //             axios.get(URL + `paintingEntity/${auth.authData.paintingLiked[i]}`).then((response) => {
    //                 let data = response.data;
    //                 let parsedData = {
    //                     id: data.id,
    //                     imageUri: URL + `images/content/mini/${data.artist_idtf_intern}/${data.generatedId}`,
    //                     artistName: data.artist_name_tot,
    //                     artworkName: data.aw_title_Eng,
    //                     artistId: data.artist_idtf_intern
    //                 }
    //
    //                 setPainting(painting => [...painting, parsedData]);
    //             });
    //     }
    // }, [auth.authData.paintingLiked, auth.refreshKey]);

    //IF THE USER HAS LIKED SOME ARTWORKS THEN SHOW IN CAROUSEL ELSE SHOW
    return (
        <View style={styles.container}>

            {/*IF CLICK ON TITLE THEN NAVIGATE TO DETAILED PAGE FULL OF ARTWORKS*/}
            <TouchableOpacity onPress={() => navigation.navigate("AllMyArtworksPage")}>
                <View style={styles.textAndChevron}>
                    {/*TEXT*/}
                    <Text style={styles.title}>Liked artworks</Text>
                    {/*CHEVRON*/}
                    <Entypo name={"chevron-right"} size={30} color={"white"}/>
                </View>
            </TouchableOpacity>

            {/*CAROUSEL OF RANDOM COLOR PAINTING*/}
            <ArtworkCarouselSingle data={painting}/>
        </View>
    )

    //return !auth.authData.paintingLiked || auth.authData.paintingLiked.length === 0  ? <View><Text style={styles.noLikedArtworks}>No liked artworks </Text></View> : <ArtworkCarouselDouble title={"Liked artworks"} data={painting} navigate={() => navigation.navigate("AllMyArtworksPage")}/>
}

export default LikedArtworksCarousel;

import React from 'react';
import {FlatList} from 'react-native';
import {Artwork} from "../../../types";
import {ArtworkComponent} from "../Artwork";

// PROPS
export type artworkCarouselSingleProps = {
    data: Artwork[],
}

/**
 * Component rendering a horizontally scrollable single carousel of artworks
 * @param {artworkCarouselSingleProps} props
 * @param {Artwork[] | undefined} props.data - Array of artworks
 * @constructor
 * @return ArtworkCarouselSingle
 */
export default function ArtworkCarouselSingle(props: artworkCarouselSingleProps) {

    // DECLARE COMPONENT MODEL TO INSERT IN FLATLIST
    const renderItem = ({item, index}: any) => {
        return (<ArtworkComponent id={item.id} imageUri={item.imageUri}
                                  artistName={item.artistName}
                                  artworkName={item.artworkName}
                                  artistId={item.artistId}/>)
    }

    return <FlatList data={props.data} renderItem={renderItem}
                  keyExtractor={(item) => item.id} horizontal showsHorizontalScrollIndicator={false}/>

}

import React, {useEffect, useState} from 'react';
import {SafeAreaView, Text, TouchableOpacity, View} from 'react-native';
import ArtworkGrid from "../index";
import {Artwork} from "../../../../types";
import axios from "axios";
import {URL} from "../../../services/constantes";
import styles from "./styles";
import {Entypo} from "@expo/vector-icons";
import {useNavigation} from "@react-navigation/native";

// PROPS
export type MatchingByColorGridProps = {
    color: string
}

/**
 * Component displaying an {@link ArtworkGrid} of artworks matched by color with **color**
 *
 * <img src="https://drive.google.com/uc?export=view&id=1NGyIjIQLRbswDqLS7LfZIt3i1KIdUSbe" alt="ArtworkPage" height="400">
 * @param {MatchingByColorGridProps} props
 * @param {string} props.color - Color picked by the user
 * @constructor
 * @return MatchingByColorGrid
 */
const MatchingByColorGrid = (props: MatchingByColorGridProps) => {

    const navigation = useNavigation()

    const [paintingsColorMatch, setPaintingsColorMatch] = useState<[Artwork] | []>([]);

    //API CALL TO GET PAINTINGS OF THE SAME COLOR AS COLOR
    useEffect(() => {
        axios
            .post(URL + `paintingSearch/findByColor`, {
                colorCode: props.color, sortOrder: "ASC"
            }, {headers: {"Access-Control-Allow-Origin": "*", "Content-Type": "application/json"}})
            .then((response) => {

                // PARSING DATA
                const parsedData = response.data.map((el: any) => {
                    return {
                        id: el.id,
                        imageUri: URL + `images/content/mini/${el.artistId}/${el.urlImage}`,
                        artistName: el.artisteName,
                        artworkName: el.name,
                        artistId: el.artistId
                    }
                })

                setPaintingsColorMatch(parsedData)
            });
    }, [props.color]);

    return (
        <SafeAreaView>
            <View style={styles.headerMatchingColor}>
                {/*BACK BUTTON*/}
                <TouchableOpacity style={styles.backButton} onPress={() => navigation.goBack()}>
                    <Entypo name={"chevron-left"} size={35} color={"white"}/>
                </TouchableOpacity>

                {/*LABEL OF COLOR */}
                <View style={[styles.colorHeader, {backgroundColor: `#${props.color}`}]}><Text
                    style={styles.title}>{props.color}</Text>
                </View>
            </View>

            {/*GRID OF ARTWORKS*/}
            <ArtworkGrid data={paintingsColorMatch}/>
        </SafeAreaView>
    )
}


export default MatchingByColorGrid;

import React, {useEffect, useState} from 'react';
import {SafeAreaView, ScrollView, Text, TouchableOpacity, View} from 'react-native';
import ArtworkGrid from "../index";
import {URL} from "../../../services/constantes";
import styles from "../styles";
import {Entypo} from "@expo/vector-icons";
import {useNavigation} from "@react-navigation/native";
import DropDownPicker from 'react-native-dropdown-picker';
import {Artwork} from "../../../../types";

// COMPONENT OF GRID OF artworkGrid

export type MatchingByImageGridProps = {
    data: any
}

const MatchingByImageGrid = (props: MatchingByImageGridProps) => {

    const navigation = useNavigation()

    const [paintings, setPaintings] = useState<[Artwork] | []>([]);

    //API CALL

    useEffect(() => {
        const parsedData = props.data ?
            props.data.style.map((el: any) => {
                return {
                    id: el.id,
                    imageUri: URL + `images/content/mini/${el.artistId}/${el.urlImage}`,
                    artistName: el.artisteName,
                    artworkName: el.name,
                    artistId: el.artistId
                }
            }) : null
        setPaintings(parsedData)
    }, [props.data])

    return (
        <SafeAreaView>
            <TouchableOpacity style={styles.backButton} onPress={navigation.goBack}>
                <Entypo name={"arrow-left"} size={30} color={"black"}/>
            </TouchableOpacity>
            <View style={[styles.colorHeader, {backgroundColor: "gray"}]}>
                <Text style={styles.title}>Matched Paintings</Text>
            </View>
            <ArtworkGrid title={"Matched"} data={paintings}/>
        </SafeAreaView>
    )
}


export default MatchingByImageGrid;

import React from 'react';
import {FlatList, Text, View} from 'react-native';
import styles from "./styles"
import {Artwork, ArtworkGridProps} from "../../../types";
import {ArtworkComponent} from "../Artwork";
import Checkbox from 'expo-checkbox';
import {useInteractions} from "../../contexts/UserInteractions";

/**
 * Component displaying a vertical scrollable grid of artworks
 * @param {ArtworkGridProps} props
 * @param {string} [props.title] - Name of the grid
 * @param {Artwork[] | []} props.data - Array of artworks
 * @constructor
 * @return ArtworkGrid
 */
export default function ArtworkGrid(props: ArtworkGridProps) {

    const userInt = useInteractions()

    // FUNCTION TO RENDER ITEM ACCORDING TO AUTH.EDIT?
    const renderItem = ({item, index}: any) => {
        return (
            <View key={index}
                  style={{justifyContent: "center", alignContent: "center", alignSelf: "center", alignItems: "center"}}>
                <ArtworkComponent id={item.id} imageUri={item.imageUri} artistName={item.artistName}
                                  artworkName={item.artworkName} artistId={item.artistId}/>
                {userInt.edit && <Checkbox style={styles.checkbox} disabled={false} value={userInt.selected.includes(item.id)} color="white"/>}
            </View>
        )
    }

    return (
        <View style={styles.container}>

            {props.title
                ? <Text style={styles.title}>{props.title}</Text>
                : <View style={styles.noTitle}/>}

            {/*ARTWORK GRID*/}
            <View style={styles.grid}>
