        const formData = new FormData();

        formData.append('image', {
            // @ts-ignore
            uri: result.uri,
            type: result.type + "/" + result.uri.split("/")[11],
            name: result.uri.split("/")[11]
        });

        xhr.send(formData);

        xhr.onreadystatechange = function () {
            if (xhr.status === 200) {
                Toast.show({
                    type: "dayl",
                    props: {
                        title: "Success",
                        text: "Changed banner",
                    }
                })
                xhr.abort()
