                {/*AVATAR ICON*/}
                <View>
                    <Image style={styles.avatarImage}
                           source={{uri: URL + `images/contentUser/${auth.authData.id}/${auth.authData.iconImage}`}}
                           resizeMode={"cover"}/>
                </View>

                {/*EDIT ICON*/}
                <TouchableOpacity style={styles.editIconContainer} onPress={() => {
                    pickImageProfile()
                    auth.setRefreshKey(auth.refreshKey + 1)
                }}>
                    <Feather name={"edit-2"} size={20} color={"white"}
                             style={{justifyContent: "center", alignItems: "center", alignSelf: "center", zIndex: 21}}/>
                </TouchableOpacity>
            </View>

        </Animated.View>
