// PROPS
export type HeaderListProps = {
    listName: string,
    scrollY: Animated.Value,
    fourImages: Artwork[] | [],
}

/**
 * Component displaying a header for {@link ListContentPage}
 *
 * <img src="https://drive.google.com/uc?export=view&id=" alt="ArtworkPage" height="200">
 * @param {HeaderListProps} props
 * @param {string} props.listName - Name of the list
 * @param {Animated.Value} props.scrollY - Value of the scroll position
 * @param {Artwork[] | []} props.fourImages - Array of 4 artworks
 * @constructor
 * @return HeaderComponentList
 */
const HeaderListComponent = (props: HeaderListProps) => {

    const parseFourImages = () => {
        return props.fourImages.length > 3 ? props.fourImages.slice(0, 4).map((el: Artwork): string => {
            return el.imageUri
        }) : props.fourImages.length > 0 ? props.fourImages.slice(0, 1).map((el: Artwork): string => {
            return el.imageUri
        }) : []
    }

    return (
        <HeaderComponentList title={props.listName} type={HeaderType.List} scrollY={props.scrollY}
                             fourImage={parseFourImages()}/>
    )
}


