import React from 'react';
import {Animated, Image, TouchableOpacity, View} from 'react-native';
import styles from "./styles";
import {AntDesign, FontAwesome} from "@expo/vector-icons";
import {LinearGradient} from "expo-linear-gradient";
import {HeaderComponentProps, HeaderType} from "../../../types";
import {useInteractions} from "../../contexts/UserInteractions";
import HeaderListComponent from "../HeaderComponent/HeaderListComponent";

// CONSTANTS
export const HEADER_MAX_HEIGHT = 250;
export const HEADER_MIN_HEIGHT = 80;
export const HEADER_SCROLL_DISTANCE = HEADER_MAX_HEIGHT - HEADER_MIN_HEIGHT;
export const SUBSTRACT_START_TRANSITION = 30
export const ICON_TRANSITION = 20

/**
 * Component displaying a header for {@link HeaderListComponent}
 * @param {HeaderComponentProps} props
 * @param {string} [props.imageUri] - URI of the image
 * @param {string} props.title - Title of the header
 * @param {string} [props.artistName] - Name of the artist
 * @param {HeaderType} props.type - Type of header
 * @param {Animated.Value} props.scrollY - Value of the scroll position
 * @param {string} [props.avatarIconUri] - URI of the icon image
 * @param {string[]} [props.fourImages] - Array of four images uri
 * @param {string} [props.IdArtwork] - ID of the artwork
 * @param {void} [props.addToList] - Toggles visibility AddToListModal
 * @param {boolean} [props.onClickImage] - Visibility onClickImageModal
 * @param {void} [props.setOnClickImage] - Toggles visibility OnClickImageModal
 * @constructor
 * @return HeaderComponentList
 */
export function HeaderList(props: HeaderComponentProps) {

    const userInt = useInteractions()

    //ZOOM IMAGE WHEN NEGATIVE SCROLL INTERPOLATE
    // @ts-ignore
    const imageScale = props.scrollY.interpolate({
        inputRange: [-100, 0], outputRange: [1.5, 1], extrapolateLeft: "extend", extrapolateRight: "clamp"
    })

    //OPACITY OF ICONS THAT SCROLL INTERPOLATE
    // @ts-ignore
    const iconsMovingOpacity = props.scrollY.interpolate({
        inputRange: [0, HEADER_SCROLL_DISTANCE - ICON_TRANSITION - 5, HEADER_SCROLL_DISTANCE - ICON_TRANSITION],
        outputRange: [1, 1, 0],
        extrapolate: 'clamp',
    });

    // TITLE OF ARTWORK OPACITY INTERPOLATE
    // @ts-ignore
    const artworkNameOpacity = props.scrollY.interpolate({
        inputRange: [0, HEADER_SCROLL_DISTANCE - ICON_TRANSITION - 3, HEADER_SCROLL_DISTANCE - ICON_TRANSITION],
        outputRange: [1, 1, 0],
        extrapolate: 'clamp',
    });

    //VIEW OF ICONS AND OPACITY INTERPOLATE
    // @ts-ignore
    const titleAndIconsOpacity = props.scrollY.interpolate({
        inputRange: [-20, -10, 0], outputRange: [0, 0.4, 1], extrapolate: 'clamp',
    });

    //LINEAR GRADIENT BEHIND TEXT OPACITY INTERPOLATE
    // @ts-ignore
    const linearGradientOpacity = props.scrollY.interpolate({
        inputRange: [-30, 0, HEADER_SCROLL_DISTANCE - SUBSTRACT_START_TRANSITION - 30],
        outputRange: [0, 1, 0],
        extrapolate: "clamp"

    })

    return (
        <TouchableOpacity disabled={props.type === HeaderType.User}
                          onPress={() => props.setOnClickImage ? props.setOnClickImage(!props.onClickImage) : console.log("")}>
            <Animated.View
                style={[styles.container, {transform: [{scale: props.type === HeaderType.User ? 1 : imageScale}]}]}>

                {/*IMAGE CONTAINER*/}
                <Animated.View
                    style={[styles.imageContainer, {transform: [{scale: props.type === HeaderType.User ? 1 : imageScale}]}]}>
                    {/*LIST AND AT LEAST FOUR IMAGES */}
                    {props.type === HeaderType.List && props.fourImage && props.fourImage.length > 3 ?
                        <View style={styles.backgroundImageGrid}>
                            {props.fourImage.map((item) => {
                                return <Image source={{uri: item}} style={styles.backgroundImageFour} key={item}/>
                            })}
                        </View> :
                        // LIST BUT NOT FOUR IMAGES
                        props.type === HeaderType.List ?
                            <Animated.Image style={[styles.coverImage]}
                                            source={{uri: props.fourImage && props.fourImage[0]}} resizeMode={'cover'}
                            /> :
                            // NOT LIST
                            <Animated.Image style={styles.coverImage} source={{uri: props.imageUri}}
                                            resizeMode={'cover'}
                            />}
                </Animated.View>

                {/*AVATAR ICON ? */}
                {props.avatarIconUri && <View style={styles.avatarContainer}>
                    <Image style={styles.avatarImage} source={{uri: props.avatarIconUri}} resizeMode={"cover"}/>
                </View>}

                {/*BACK BUTTON*/}
                {/*{props.type !== HeaderType.User && <BackButton/>}*/}

                {/*Title and icons? container*/}
                <Animated.View style={[styles.titleAndIcons, {opacity: titleAndIconsOpacity}]}>
                    {/*LINEAR GRADIENT BEHIND TEXT*/}
                    <Animated.View style={[styles.linearGradientContainer, {opacity: linearGradientOpacity}]}>
                        <LinearGradient colors={['black', 'transparent']} start={{x: 0, y: 1,}} end={{x: 0, y: 0.5}}
                                        style={[styles.linearGradient]}/>
                    </Animated.View>
