/**
 * Component displaying a modal letting the user edit a list
 *
 * <img src="https://drive.google.com/uc?export=view&id=1c6IeRHssXP1bXC50RlktxiE2JDBWyGJL" alt="ArtworkPage" height="200">
 * @param {EditListModalProps} props
 * @param {string} props.listId - ID of the list
 * @param {boolean} props.modalVisible - Visibility of the modal
 * @param {void} props.setModalVisible - Toggles the visibility of the modal
 * @constructor
 * @return Modal
 */
export default function EditListModal(props: EditListModalProps) {

    const userInt = useInteractions()

    const navigation = useNavigation()
    const [newNameList, setNewNameList] = useState("")
    const [confirmDelete, setConfirmDelete] = useState(false)

    const auth = useAuth()

    const showConfirmDialog = () => {
        return Alert.alert(
            "Are your sure?",
            "Are you sure you want to delete this list?",
            [
                // The "Yes" button
                {
                    text: "Yes",
                    onPress: () => {
                        userInt.deleteList(props.listId)
                        navigation.goBack()
                        auth.reloadData()
                        setConfirmDelete(false);

                    },
                },
                // The "No" button
                // Does nothing but dismiss the dialog when tapped
                {
                    text: "No",
                    onPress: () => {
                        setConfirmDelete(false)
                    }
                },
            ]
        );
    };

    //SWIPE DOWN TO DISMISS MODAL
    return (
        <GestureRecognizer onSwipeDown={() => props.setModalVisible(false)}>
            {/*MODAL */}
            <Modal animationType={"slide"} transparent={true} visible={props.modalVisible} onRequestClose={() => {
                props.setModalVisible(!props.modalVisible);
            }}>
                {/*MODAL VIEW*/}
                <View style={styles.containerModal}>
                    {/*CLOSE MODAL*/}
                    <TouchableOpacity style={styles.cross} onPress={() => props.setModalVisible(false)}>
                        <Entypo name={"cross"} size={25} color={"white"}/>
                    </TouchableOpacity>
                    {/*DELETE LIST BUTTON*/}
                    <TouchableOpacity style={styles.deleteList} onPress={() => setConfirmDelete(true)}>
                        <Octicons name={"trash"} size={25} color={"white"}/>
                    </TouchableOpacity>
                    {/*EDIT TITLE*/}
                    <Text style={styles.renameText}>Edit</Text>
                    {/*TEXT INPUT FOR RENAMING LIST*/}
                    <TextInput style={styles.textInput} placeholder={"Rename"} placeholderTextColor={"white"}
                               value={newNameList} onChangeText={setNewNameList}></TextInput>
                    {/*SUBMIT BUTTON*/}
                    <TouchableOpacity style={styles.submitButton}
                                      onPress={() => userInt.updateList(props.listId, newNameList)}>
                        <Text style={styles.submitText}>SUBMIT</Text>
                    </TouchableOpacity>
                    {/*CONFIRM MESSAGE IF DELETE*/}
                    {confirmDelete && showConfirmDialog()}
                </View>
            </Modal>
        </GestureRecognizer>
    )
}

