    const [loading, setLoading] = useState(false)

    const navigation = useNavigation()
    const data = {itemName: " ", artistName: " "}

    // API CALL TO POST NEW ICON IMAGE
    const sendDataProfile = (result: any) => {


        var image = new FormData();

