// PROPS
export type NewListModalProps = {
    modalNewList: boolean,
    setModalNewList(modalNewList: boolean): void,
}

/**
 * Component displaying a modal letting the user create a new list
 *
 * <img src="https://drive.google.com/uc?export=view&id=1Wfk4UNBoJrPdj5u-Oe32GG-95qi_1wAz" alt="ArtworkPage" height="500">
 * @param {NewListModalProps} props
 * @param {boolean} props.modalNewList - Visibility of the modal
 * @param {void} props.setModalVisible - Toggles the visibility of the modal
 * @constructor
 * @return Modal
 */
export default function NewListModal(props: NewListModalProps) {

    const auth = useAuth()

    const userInt = useInteractions()

    const [listName, setListName] = useState("")

    // WHEN CLICKING ON SUBMIT BUTTON
    const onPressSubmitNewList = (listName: string) => {
        if (listName) {
            userInt.createNewList(listName);
            props.setModalNewList(false);
            auth.reloadData()

        } else {
            Toast.show({
                    type: "dayl",
                    props: {
                        title: "Failed",
                        text: "No name was given to the list !",
                    }
                }
            )
        }
    }

    return (
        <Modal isVisible={props.modalNewList} backdropOpacity={0.80}
               onBackdropPress={() => {
                   props.setModalNewList(!props.modalNewList)
               }}
               onModalHide={() => setListName("")}
               animationIn={"fadeIn"}
               animationOut={"fadeOut"}
               backdropTransitionOutTiming={0} /*fix flickering*/
               statusBarTranslucent={true}>
            <View style={styles.modal}>

                {/*HEADER*/}
                <View style={styles.modalHeader}>
                    {/*BACK BUTTON*/}
                    <TouchableOpacity onPress={() => {
                        props.setModalNewList(false)
                    }}>
                        <Entypo name={"cross"} size={25} color={"white"}/>
                    </TouchableOpacity>

                    {/*TITLE OF THE MODAL*/}
                    <Text style={styles.modalTitle}>Add new list</Text>
                </View>

                {/*INPUT FOR NAME OF THE LIST*/}
                <TextInput style={styles.input} placeholder="New list name" placeholderTextColor={"gray"}
                           value={listName} onChangeText={setListName}/>

                {/*SUBMIT BUTTON*/}
                <TouchableOpacity style={styles.submitButton} onPress={() => {
                    onPressSubmitNewList(listName)
                }}>
                    <Text style={styles.submit}>Create</Text>
                </TouchableOpacity>

            </View>

            {/*TOAST CONFIG*/}
            <Toast config={toastConfig}/>
        </Modal>
    )
}

