export type NewMessageModalProps = {
    isVisible: boolean,
    setVisible(isVisible: boolean): void,
    setNewDiscussion(newDiscussion: Discussion): void,
}

/**
 * Component displaying a modal letting the user create a new discussion with a user
 *
 * <img src="https://drive.google.com/uc?export=view&id=1YCfeQI7fUjZT4SgFXLHWrnWxzItR5wNC" alt="ArtworkPage" height="400">
 * @param {NewMessageModalProps} props
 * @param {boolean} props.isVisible - Modal visibility
 * @param {void} props.setVisible(boolean) - Function for toggling modal visibility
 * @param {void} props.setNewDiscussion(Discussion) - Function for creating the new discussion
 * @constructor
 * @return Modal
 */
export default function NewMessageModal(props: NewMessageModalProps) {

    const navigation = useNavigation()
    const [clicked, setClicked] = useState(false)
    const [searchPhrase, setSearchPhrase] = useState("")
    const [userList, setUserList] = useState([])

    const getDate = () => {
        return new Date().toLocaleTimeString()
    }

    useEffect(() => {
        setUserList([])
        axios.get(URL + "users/all")
            .then((response) => {
                const parsedData = response.data.map((el: any) => {
                    return {
                        id: el.id,
                        firstName: el.firstName,
                        lastName: el.lastName,
                        iconImage: el.iconImage
                    }
                })
                const temp = parsedData.filter((item: any) => item.firstName.includes(searchPhrase))
                setUserList(temp)
            })

    }, [props.isVisible, searchPhrase])

    const handleUserClick = (item: any) => {
        props.setVisible(!props.isVisible)
        props.setNewDiscussion({
            userId: item.id.toString(),
            userName: item.firstName,
            iconImage: URL + `images/contentUser/${item.id}/${item.iconImage}`,
            date: getDate(),
            lastMessage: "Love your art !"
        })
        navigation.navigate("ChatPage", {
            userId: item.id,
            firstName: item.firstName,
            iconImage: URL + `images/contentUser/${item.id}/${item.iconImage}`
        })
    }

    const renderItem = (item: any) => {
        return (
            <TouchableOpacity style={styles.resultButton} onPress={() => handleUserClick(item)}>
                <Image style={styles.avatar}
                       source={{uri: item.iconImage ? URL + `images/contentUser/${item.id}/${item.iconImage}` : "https://vetref.fr/wp-content/uploads/2021/02/blank-profile-picture-973460_640.png"}}/>
                <Text style={styles.resultText}>{item.firstName}</Text>
            </TouchableOpacity>
        )
    }

    return (
        <Modal
            style={styles.modal}
            isVisible={props.isVisible}
            backdropOpacity={0}
            swipeDirection={["down"]}
            propagateSwipe={true}
            backdropTransitionInTiming={0}
            backdropTransitionOutTiming={0}
            hideModalContentWhileAnimating={true}
            onSwipeComplete={() => props.setVisible(!props.isVisible)}
            statusBarTranslucent={true}
            onBackButtonPress={() => props.setVisible(!props.isVisible)}
        >
            <View style={styles.container}>
                <Text style={styles.title}>Create new chat</Text>
                <SmallerSearchBar clicked={clicked} searchPhrase={searchPhrase} setSearchPhrase={setSearchPhrase}
                                  setClicked={setClicked}/>
                <ScrollView style={styles.resultContainer}>
                    {userList.map((item) => {
                        return renderItem(item)
                    })}
                </ScrollView>
            </View>
        </Modal>
    )
}

