import {useEffect, useState} from "react";
import axios from "axios";
import MoreInformation from "../index";
import {URL} from "../../../services/constantes"
import {ArtworkMoreInfoType} from "../../../../types";

// PROPS
export type ArtistMoreInfoProps = {
    artistId: string
}

/**
 * @ignore Not used yet
 * Component displaying a {@link MoreInformation} component for artists
 * @param {ArtistMoreInfoProps} props
 * @param {string} props.artworkId - ID of the artist
 * @constructor
 * @return MoreInformation
 */
export default function ArtistMoreInfo(props: ArtistMoreInfoProps) {

    // USESTATE TO STORE DATA FROM API FETCH
    const [data, setData] = useState<ArtworkMoreInfoType>({
        artworkName: "",
        artistName: "",
        artistDOB: "",
        artistDOD: "",
        date: "",
        dimensions: "",
        genre: "",
        keywords: [],
        style: "",
        summary: ""
    });

    // API CALL TO GET ALL DATA OF AN ARTIST
    useEffect(() => {
        axios
            .get(URL + `paintingEntity/${props.artistId}`, {
                headers: {
                    "Access-Control-Allow-Origin": "*",
                    "Content-Type": "application/json"
                }
            })
            .then((response) => {

                // PARSING DATA
                const aw = response.data;
                const keywords = aw.aw_keyw_import ? aw.aw_keyw_import.slice(1, -1).split(",") : null
                let result = []
                if (keywords) {
                    for (let i = 0; i < keywords.length; i++) {
                        result.push({
                            name: keywords[i],
                            id: i.toString()
                        })
                    }
                }

                setData({
                    artworkName: aw.aw_title_Origin,
                    artistName: aw.artist_name_tot,
                    date: aw.aw_year,
                    artistDOB: aw.artist_birthyear,
                    artistDOD: aw.artist_deathyear,
                    summary: aw.aw_desc_basic_1,
                    style: aw.aw_style_import,
                    // Check if dimensions are null, if so return null
                    dimensions: aw.aw_dim_height != null && aw.aw_dim_width != null ? `${aw.aw_dim_height} x ${aw.aw_dim_width} cm` : null,
                    genre: aw.aw_genre_import,
                    // Parse keywords string into list
                    keywords: result ? result : null,
                });
            })
    }, [props.artistId])

    // @ts-ignore
    return (<MoreInformation data={data}/>)
}

