                // PARSING DATA
                const aw = response.data;
                const keywords = aw.aw_keyw_import ? aw.aw_keyw_import.slice(1, -1).split(",") : null
                let test = []
                if (keywords) {
                    for (let i = 0; i < keywords.length; i++) {
                        test.push({
                            name: keywords[i],
                            id: i.toString()
                        })
                    }
                }

                setData({
                    artworkName: aw.aw_title_Origin,
                    artistName: aw.artist_name_tot,
                    date: aw.aw_year,
                    artistDOB: aw.artist_birthyear,
                    artistDOD: aw.artist_deathyear,
                    summary: aw.aw_desc_basic_1,
                    style: aw.aw_style_import,
                    // Check if dimensions are null, if so return null
                    dimensions: aw.aw_dim_height != null && aw.aw_dim_width != null ? `${aw.aw_dim_height} x ${aw.aw_dim_width} cm` : null,
                    genre: aw.aw_genre_import,
                    // Parse keywords string into list
                    keywords: test ? test : null,
                });
            })
    }, [props.artworkId])

    // @ts-ignore
    return (<MoreInformation data={data}/>)
}

