 * @constructor
 * @return ToolBarPost
 */
export default function ToolBarComment(props: ToolBarCommentProps) {

    const navigation = useNavigation()

    const [isLiked, setIsLiked] = useState(false)

    const auth = useAuth()

    const userInt = useInteractions()

    const handleLikeClick = () => {
        // if (isLiked) {
        //     axios.delete(URL + "api/publication/likePubli/removeLike", { params: { id: props.publicationId } });
        //     userInt.setRefreshKeySM(userInt.refreshKeySM+1)
        //
        // } else {
        //
        //     axios.post(URL + "api/publication/addLikeToComment",
        //         {
        //             publicationOrCommentId: props.commentId,
        //             userId: auth.authData.id,
        //             likeType: "string"
        //         }, {headers: {"Access-Control-Allow-Origin": "*", "Content-Type": "application/json"}}
        //     )
        //     userInt.setRefreshKeySM(userInt.refreshKeySM+1)
        // }
        // setIsLiked(!isLiked)
    }

    useEffect(() => {
        axios.get(URL + 'api/publication/commentPubli/byId', {params: {id: props.commentId}}).then((response) => {
            setNumberLikes(response.data.listLike.length)
            let i = 0;
            while (i < response.data.listLike.length && response.data.listLike[i].id !== props.commentId)
                i++
            setIsLiked(response.data.listLike[i].id === props.commentId)

        });
    }, [])

    const handleCommentClick = () => {
        // navigation.navigate("CommentsPage", {publicationId: props.publicationId})
    }

    const [numberLikes, setNumberLikes] = useState(0)
    const [numberComments, setNumberComments] = useState(0)


    useEffect(() => {
        axios.get(URL + 'api/publication/byId', {params: {id: props.publicationId}}).then((response) => {
            setNumberLikes(response.data.listLike.length)
        });
        axios.get(URL + 'api/publication/commentPubli/ByPublicationId', {params: {id: props.publicationId}}).then((response) => {
            setNumberComments(response.data.length)
        });
    }, [props.publicationId, userInt.refreshKeySM]);

    return (
        <View style={styles.container}>
            {/*LIKES AND COMMENTS*/}
            <View style={styles.leftIcons}>
                {/*LIKES*/}
