/**
 * Component used to display an {@link ArtistGrid} of the user's liked artists
 *
 * <img src="https://drive.google.com/uc?export=view&id=1ajKdUL-It46giiiWuFwQqX35Q4KpzJNg" alt="ArtworkPage" height="300">
 * @constructor
 * @return ArtistGrid
 */
export default function MyLikedArtists(props: MyLikedArtistsProps) {

    const [myLikedArtists, setMyLikedArtists] = useState<[Artist] | []>([]);
    const auth = useAuth()
    const navigation = useNavigation()

    if (!props.id) {


        //API call (list of similar artist)
        useEffect(() => {
            setMyLikedArtists([])
            if (auth.authData.artistLiked) {
                auth.authData.artistLiked.forEach(artist => {
                    axios.get(URL + `artist/${artist}`).then((response) => {
                        axios.get(URL + `paintingEntity/byArtist/${response.data.artistIdtfIntern}/0`).then((response2) => {

                            // PARSING DATA
                            if (response2.data[0].urlImage) {
                                //@ts-ignore
                                setMyLikedArtists(myLikedArtists => [...myLikedArtists, {
                                    id: response.data.artistIdtfIntern,
                                    title: response.data.artist_name_tot,
                                    imageUri: URL + `images/content/mini/${response.data.artistIdtfIntern}/${response2.data[0].urlImage}`
                                }].slice(0, 4))
                            } else {
                                //@ts-ignore
                                setMyLikedArtists(myLikedArtists => [...myLikedArtists, {
                                    id: response.data.artistIdtfIntern,
                                    title: response.data.artist_name_tot,
                                    imageUri: URL + `images/content/mini/${response.data.artistIdtfIntern}/${response2.data[1].urlImage}`
                                }].slice(0, 4))
                            }
                        })
                    });
                })
            }
        }, [auth.authData.artistLiked]);
    }
    else {
        //API call for another user(list of similar artist)
        useEffect(() => {
            setMyLikedArtists([])
            axios.get(URL + `users/${props.id}`).then((userResponse) => {
                userResponse.data.artistLiked.forEach(artist => {
                    axios.get(URL + `artist/${artist}`).then((response) => {
                        axios.get(URL + `paintingEntity/byArtist/${response.data.artistIdtfIntern}/0`).then((response2) => {

                            // PARSING DATA
                            if (response2.data[0].urlImage) {
                                //@ts-ignore
                                setMyLikedArtists(myLikedArtists => [...myLikedArtists, {
                                    id: response.data.artistIdtfIntern,
                                    title: response.data.artist_name_tot,
                                    imageUri: URL + `images/content/mini/${response.data.artistIdtfIntern}/${response2.data[0].urlImage}`
                                }].slice(0, 4))
                            } else {
                                //@ts-ignore
                                setMyLikedArtists(myLikedArtists => [...myLikedArtists, {
                                    id: response.data.artistIdtfIntern,
                                    title: response.data.artist_name_tot,
                                    imageUri: URL + `images/content/mini/${response.data.artistIdtfIntern}/${response2.data[1].urlImage}`
                                }].slice(0, 4))
                            }
                        })
                    });
                })
            })
        }, [props.id]);
    }
    //RETURN
    return !auth.authData.artistLiked || auth.authData.artistLiked.length === 0  ? <View><Text style={styles.noLikedArtists}>No liked artists </Text></View> : (
        <ArtistGrid title={"My liked artists"} data={myLikedArtists} navigate={() => navigation.navigate("AllMyArtistsPage")}/>
    )

}

