}

/**
 * Component used to display a grid of 4 similar artists
 *
 * <img src="https://drive.google.com/uc?export=view&id=1tqlaBZP2Zc17YwnecW01Ftg8_7Igb26W" alt="ArtworkPage" height="300">
 * @param {SimilarArtistsProps} props
 * @param {string} props.artistId - ID of the artist
 * @constructor
 * @return ArtistGrid
 * */
export default function SimilarArtists(props: SimilarArtistsProps) {

    const [listSimilarArtist, setListSimilarArtist] = useState<[Artist] | []>([]);

    //API CALL (list of similar artist)
    useEffect(() => {

        axios.get(URL + `similarArtists/findByArtistId/${props.artistId}/?numberOfArtists=49`).then((response) => {

            // PARSING DATA
            let data = response.data.filter(function (el: any) {
                return el.artistDisplayDTO != null;
            }).map(function (el: any) {
                return {
                    id: el.artistDisplayDTO.functionalId,
                    imageUri: URL + `images/content/mini/${el.artistDisplayDTO.functionalId}/${el.artistDisplayDTO.backgroundImage}`,
                    title: el.artistDisplayDTO.name
                }
            })
            const fourArtist = data.slice(1, 5)

            setListSimilarArtist(fourArtist)
        });
    }, [props.artistId]);

    return (
        <ArtistGrid title={"Similar artists"} data={listSimilarArtist}/>
    )
}

