import {useNavigation} from "@react-navigation/native";
import {LinearGradient} from "expo-linear-gradient";
import {PageTypeList} from "../../../../types";
import {useInteractions} from "../../../contexts/UserInteractions";
import AllMyArtworks from "../../../screens/DetailedPages/AllMyArtworks";

// PROPS
export type ToolBarListPageProps = {
    type: PageTypeList,
    listId: string | undefined,
    modalVisible: boolean,
    setModalVisible(modalVisible: boolean): void,
    addToListModal: boolean,
    setAddToListModal(addToListModal: boolean): void,
}

/**
 * Component displaying the toolbar for the {@link ListContentPage} and {@link AllMyArtworks}
 *
 * <img src="https://drive.google.com/uc?export=view&id=1RD3birE2392NqlnnwkHW18EHkc-cnecG" alt="ArtworkPage" height="100">
 * @param {ToolBarListPageProps} props
 * @param {PageTypeList} props.type - Type of page
 * @param {string} props.listId - ID of the list
 * @param {boolean} props.modalVisible - Visibility of the EditList modal
 * @param {void} props.setModalVisible - Toggles the visibility of the EditList modal
 * @param {boolean} props.addToListModal - Visibility of the AddToList modal
 * @param {void} props.setAddToListModal - Toggles the visibility of the AddToList modal
 * @constructor
 * @return ToolBarListPage
 */
export default function ToolBarListPage(props: ToolBarListPageProps) {

    const auth = useAuth()
    const userInt = useInteractions()
    const navigation = useNavigation()

    return (
        <SafeAreaView style={styles.container}>
            <Animated.View style={[styles.linearGradientContainer, {opacity: 1}]}>
                <LinearGradient colors={['transparent', 'black']} start={{x: 0, y: 1,}} end={{x: 0, y: 0.5}}
                                style={[styles.linearGradient]}/>
            </Animated.View>

            {/*BACK BUTTON*/}
            <TouchableOpacity style={styles.backButton} onPress={() => {
                userInt.setEdit(false);
                auth.reloadData();
                navigation.goBack()
            }}>
                <Entypo name={"chevron-left"} size={35} color={"white"}/>
            </TouchableOpacity>

            {props.type !== PageTypeList.OtherList &&
                <View style={styles.RightContainer}>
                    {/* ADD MULTIPLE ELEMENTS TO LIST*/}
                    {userInt.edit && <TouchableOpacity style={styles.icon} disabled={userInt.selected.length === 0}
                                                       onPress={() => {
                                                           props.setAddToListModal(!props.addToListModal);
                                                           auth.setRefreshKey(auth.refreshKey + 1)
                                                       }}>
                        <FontAwesome name={"bookmark-o"} size={25}
                                     color={userInt.selected.length > 0 ? "white" : "grey"}/>
                    </TouchableOpacity>
                    }
                    {/*DELETE SELECTED ELEMENTS*/}
                    {userInt.edit && <TouchableOpacity style={styles.icon} disabled={userInt.selected.length === 0}
                                                       onPress={() => {
                                                           userInt.deleteArtworks(props.listId);
                                                           auth.setRefreshKey(auth.refreshKey + 1);
                                                           auth.reloadData()
                                                       }}>
                        <Octicons name={"trash"} size={25} color={userInt.selected.length > 0 ? "white" : "grey"}/>
                    </TouchableOpacity>
                    }

                    {/* EDIT ELEMENTS BUTTON */}
                    <TouchableOpacity style={styles.icon} onPress={() => {
                        userInt.setEdit(!userInt.edit);
                        userInt.setSelected([])
                    }}>
                        <MaterialCommunityIcons
                            name={!userInt.edit ? "checkbox-multiple-blank-circle-outline" : "checkbox-multiple-marked-circle-outline"}
                            size={25} color={"white"}/>
                    </TouchableOpacity>

                    {/*THREE DOTS TO EDIT LIST*/}
                    {props.type != PageTypeList.LikedArtworks &&
                        <TouchableOpacity style={styles.icon} onPress={() => {
                            props.setModalVisible(!props.modalVisible)
                        }}>
                            <Entypo name={"dots-three-horizontal"} size={25} color={"white"}/>
                        </TouchableOpacity>}

                </View>}
        </SafeAreaView>
    )
}

import {Animated, StyleSheet, View} from 'react-native';
