/**
 * @ignore
 * @param route
 * @constructor
 */
export default function ArtistNonUserPage({route}: any) {

    //GET ROUTE DATA

    const {
        imageUri,
        artistName,
        artistId,
    }: {
        imageUri: string,
        artistName: string,
        artistId: string,
    } = route.params

    //ON CLICK USE STATE

    const [onClickImage, setOnClickImage] = useState(false)

    //SCROLLY ANIMATION VALUE

    const scrollY = useRef(new Animated.Value(0)).current;

    //DEACTIVATE TOP BAR WHEN SCROLLY IS ABOVE CERTAIN AMOUNT

    function deactivateTopBar(event: any) {
        if (event.nativeEvent.contentOffset.y > 90)
            setPointerEvenTopBar("auto")
        else
            setPointerEvenTopBar("none")
    }

    //USE STATE ACTIVATE/DEACTIVATE TOP BAR

    let [pointerEventTopBar, setPointerEvenTopBar] = useState<any>("none")

    return (
        <View>
            {/*ON IMAGE CLICK OPEN ZOOM*/}
            <OnClickImage imageUri={imageUri} title={artistName} onClickImage={onClickImage}
                          setOnClickImage={setOnClickImage}/>

            {/*ANIMATED TOP BAR THAT APPEARS WHEN SCROLL DOWN*/}
            <View style={[styles.topBar]} pointerEvents={pointerEventTopBar}>
                <TopBarAnimatedHeader title={artistName} type={HeaderType.NonUser} scrollY={scrollY}/>
            </View>

            {/*ANIMATED SCROLL VIEW*/}
            <Animated.ScrollView alwaysBounceVertical={true} scrollEventThrottle={16}
                                 showsVerticalScrollIndicator={false}
                                 onScroll={Animated.event([{nativeEvent: {contentOffset: {y: scrollY}}}], {
                                     useNativeDriver: true,
                                     listener: (event: Object) => deactivateTopBar(event)
                                 })}>
                {/*HEADER COMPONENT*/}
                <HeaderArtistNonUser imageUri={imageUri} artistName={artistName} scrollY={scrollY}
                                     onClickImage={onClickImage} setOnClickImage={setOnClickImage}/>
                {/*BACK BUTTON*/}
                <BackButton/>
                {/*FOLLOWING BUTTON*/}
                <FollowingButtonComponent artistId={artistId}/>
                {/*SUMMARY OF ARTWORK*/}
                {/*<ArtistTabView artistId={artistId} information={information}/>*/}
            </Animated.ScrollView>
        </View>

    )

}

