/**
 * Page on which the user can discover new artworks and artists
 *
 * <img src="https://drive.google.com/uc?export=view&id=1B_EqzN75vi7ylrlq_GALFH1xL5RpdhGA" alt="ArtworkPage" height="500">
 * @constructor
 * @return ExplorePage
 */
export default function ExplorePage() {

    const [searchPhrase, setSearchPhrase] = useState<string>("");
    const [clicked, setClicked] = useState(false);
    const [data, setData] = useState<[Artwork] | []>([]);
    const [filter, setFilter] = useState({styleFilter: "", genreFilter: "", nationalityFilter: ""})
    const ref = React.useRef(null);

    // API POST CALL TO GET THE RESULT OF THE SEARCH WITH THE SEARCHPHRASE
    useEffect(() => {
        setData([])

        axios.post(URL + "paintingSearch/findPaintingBySearchDto", {
            license: "",
            style: filter.styleFilter,
            genre: filter.genreFilter,
            nationality: filter.nationalityFilter,
            freeSearch: searchPhrase
        }, {headers: {"Access-Control-Allow-Origin": "*", "Content-Type": "application/json", "USER_PAINT": "0"}})
            .then((response) => {
                const parsedData: [Artwork] | [] = response.data.map((el: any) => {
                    return {
                        id: el.id,
                        imageUri: URL + `images/content/mini/${el.artistId}/${el.urlImage}`,
                        artistName: el.artisteName,
                        artworkName: el.name,
                        artistId: el.artistId
                    }
                })
                setData(parsedData)
            });
    }, [searchPhrase, filter])

    useScrollToTop(ref);

    return (
        <SafeAreaView style={styles.container}>
            <ScrollView horizontal={false} keyboardShouldPersistTaps={"handled"} ref={ref}>
                {/*SEARCHBAR*/}
                <NewSearchBar
                    searchPhrase={searchPhrase}
                    setSearchPhrase={setSearchPhrase}
                    clicked={clicked}
                    setClicked={setClicked}
                    filter={filter}
                    setFilter={setFilter}
                />
                {/*DISPLAYS ExploreComponent (default) | SearchArtworkGridComponent (if text in searchBar or filter activated)*/}
                {searchPhrase !== "" || filter.styleFilter !== "" || filter.genreFilter !== "" || filter.nationalityFilter !== ""
                    ? (<SearchArtworkGridComponent searchPhrase={searchPhrase} setClicked={setClicked} data={data}/>)
                    : <ExploreComponent/>}
            </ScrollView>
        </SafeAreaView>
    )
}

