import React from 'react';
import {Image, Text, TouchableOpacity, View} from 'react-native';
import {useNavigation} from "@react-navigation/native";
import styles from "./styles"
import {useAuth} from "../../contexts/Auth";
import {useInteractions} from "../../contexts/UserInteractions";
import {LOREM} from "../../services/constantes";

// PROPS
export type artworkProps = {
    id: string;
    imageUri: string;
    artistName: string;
    artworkName: string;
    artistId: string,
}

/**
 * Component displaying an Artwork with image, name and artist name
 *
 * <img src="https://drive.google.com/uc?export=view&id=19vamBJAY1JchpK47QNtNfrkuGSZwz120" alt="AllMyArtistsMockup" height="500">
 * @param {artworkProps} props
 * @param {string} props.id - ID of the artwork
 * @param {string} props.imageUri - URI of the image
 * @param {string} props.artistName - Name of the Artist
 * @param {string} props.artworkName - Name of the artwork
 * @param {string} props.artistId - ID of the artist
 * @return ArtworkComponent
 */
export function ArtworkComponent(props: artworkProps) {

    const navigation = useNavigation();

    const auth = useAuth()

    const userInt = useInteractions()

    // WHEN CLICKED NAVIGATES TO ARTWORK PAGE
    const onPressArtwork = () => {
        navigation.navigate('ArtworkPage', {
            id: props.id,
            imageUri: props.imageUri,
            information: LOREM,
            artworkName: props.artworkName,
            artistId: props.artistId,
            artistName: props.artistName
        })
    }

    // FUNCTION THAT FILTERS THROUGH AUTH.SELECTED TO FIND THE ACCORDING ID AND THEN REMOVES IT IF ITS PRESENT AND ADDS IT OTHERWISE
    const addToSelected = () => {
        // IF ALREADY IN LIST THEN REMOVE
        if (userInt.selected.includes(props.id))
            userInt.setSelected(userInt.selected.filter(el => el !== props.id))
        // ELSE ADD IT TO LIST
        else
            userInt.setSelected([...userInt.selected, props.id])
    }

    return (
        <View style={styles.container}>
            {/*CUSTOM BUTTON - ON PRESS NAVIGATE TOWARDS ARTWORK PAGE*/}
            <TouchableOpacity onPress={() => userInt.edit ? addToSelected() : onPressArtwork()}>
                <Image source={{uri: props.imageUri}} style={styles.image}/>
                {/*ARTWORK NAME - TEXT*/}
                <Text numberOfLines={1} style={styles.artworkName}>{props.artworkName}</Text>
                {/*ARTIST NAME - TEXT*/}
                <Text numberOfLines={1} style={styles.artistName}>{props.artistName}</Text>
            </TouchableOpacity>
        </View>
    )
}

import React, {useEffect, useState} from 'react';
import axios from "axios";
