import React, {useEffect, useState} from 'react';
import {Text, TouchableOpacity, View} from 'react-native';
import {Artwork} from "../../../types";
import ArtworkCarouselSingle from "../ArtworkCarouselSingle";
import styles from "./styles";
import {useNavigation} from "@react-navigation/native";
import axios from "axios";
import {URL} from "../../services/constantes";
import {Entypo} from "@expo/vector-icons";
import ColorPickerModal from "../Modals/ColorPickerModal";

/**
 * Function used to generate a random color when mounting the component for the first time
 * @return : string of random HEX color with "#" in front*/
const generateColor = () => {
    const randomColor = Math.floor(Math.random() * 16777215)
        .toString(16)
        .padStart(6, '0');
    return `#${randomColor}`;
};

/**
 * Color generated randomly on first mount
 * @type string
 */
export const randomColor = generateColor()

/**
 * Component displaying a {@link ArtworkCarouselSingle} of similar color artworks and a button letting the user choose the color he wants
 * @constructor
 * @return DiscoverByColor
 */
export default function DiscoverByColor() {

    const [paintingByColor, setPaintingByColor] = useState<[Artwork] | []>([]);
    const [isModalVisible, setModalVisible] = useState(false);
    const [color, setColor] = useState(randomColor)
    const navigation = useNavigation()

    // API CALL TO GET PAINTINGS OF THE SAME COLOR
    useEffect(() => {
        axios
            .post(URL + `paintingSearch/findByColor`, {
                colorCode: color.slice(1), sortOrder: "ASC"
            }, {headers: {"Access-Control-Allow-Origin": "*", "Content-Type": "application/json"}})
            .then((response) => {

                // PARSING DATA
                const parsedData = response.data.map((el: any) => {
                    return {
                        id: el.id,
                        imageUri: URL + `images/content/mini/${el.artistId}/${el.urlImage}`,
                        artistName: el.artisteName,
                        artworkName: el.name,
                        artistId: el.artistId
                    }
                })

                setPaintingByColor(parsedData)
            });
    }, [color, isModalVisible]);

    const toggleModal = () => {
        setModalVisible(!isModalVisible);
    };

    return (
        <View>
            {/*MODAL FOR COLOR PICKER*/}
            <ColorPickerModal isModalVisible={isModalVisible} setModalVisible={setModalVisible} color={color}
                              setColor={setColor}/>

            <View style={styles.container}>

                {/*IF CLICK ON TITLE THEN NAVIGATE TO PAGE FULL OF ARTWORKS*/}
                <TouchableOpacity onPress={() => navigation.navigate("ListContentPage", {
                    id: undefined,
                    name: color,
                    data: paintingByColor
                })}>
                    <View style={styles.textAndChevron}>
                        {/*TEXT*/}
                        <Text style={styles.title}>Discover by color</Text>
                        {/*CHEVRON*/}
                        <Entypo name={"chevron-right"} size={30} color={"white"}/>
                    </View>
                </TouchableOpacity>

                {/*CHOOSE COLOR BUTTON*/}
                <TouchableOpacity style={[styles.button, {backgroundColor: color}]} onPress={() => toggleModal()}>
                    <Text style={styles.buttonText}>Choose colour</Text>
                </TouchableOpacity>

                {/*CAROUSEL OF RANDOM COLOR ARTWORKS*/}
                <ArtworkCarouselSingle data={paintingByColor}/>
            </View>
        </View>)
}

