/**
 * Component displaying a modal for the user to add an {@link Artwork} to a list
 *
 * <img src="https://drive.google.com/uc?export=view&id=16poHiJGmBRyJ0TvVtaA-fpn8OUm-dEP8" alt="ArtworkPage" height="500">
 * @param {AddToListModal} props
 * @param {boolean} props.modalVisible - Visibility of the modal
 * @param {void} props.setModalVisible - Toggles the visibility of the modal
 * @constructor
 * @return Modal
 */
export default function AddToListModal(props: AddToListModalProps) {

    const [myLists, setMyLists] = useState<any>([])
    const [clicked, setClicked] = useState(false)
    const [searchPhrase, setSearchPhrase] = useState("")
    const [newData, setNewData] = useState(myLists)
    const [newListModal, setNewListModal] = useState(false)
    const auth = useAuth()

    useEffect(() => {
        props.setModalVisible(false)
    }, [auth.refreshKey])

    useEffect(() => {
        setNewData(myLists)
    }, [myLists])

    useEffect(() => {
        const temp = myLists.filter((item: { title: string | string[]; }) => item.title.includes(searchPhrase))
        setNewData(temp)
    }, [searchPhrase])

    //API CALL TO GET MY LISTS
    useEffect(() => {
        axios.get(URL + `userLists/byUserId`, {params: {userId: auth.authData.id}}).then((response1) => {

            // PARSING THE DATA
            const parsedData = response1.data.map((el: any) => {
                return {
                    id: el.id,
                    fourImages: el.imageUrl === "" ? "" : [URL + `images/content/mini/${el.imageUrl}`],
                    title: el.listName,
                    action: ListActions.detailed,
                }
            })

            setMyLists(parsedData)
        });
    }, [auth.authData.listIds, auth.refreshKey]);

    return (
        <Modal animationType={"fade"} transparent={false} visible={props.modalVisible}
               onRequestClose={() => {
                   props.setModalVisible(false)
               }} statusBarTranslucent={true}>

            <SafeAreaView style={{backgroundColor: "black", height: '100%',}}>
                <ScrollView>
                    {/*HEADER WITH BACK BUTTON + TITLE + PLUS BUTTON*/}
                    <View style={styles.headerContainer}>

                        <View style={styles.leftContainer}>
                            {/*BACK BUTTON*/}
                            <TouchableOpacity style={styles.backButtonContainer} onPress={() => {
                                props.setModalVisible(false)
                            }}>
                                <Entypo name={"chevron-left"} color="white" size={40}/>
                            </TouchableOpacity>

                            {/*MY LISTS TITLE*/}
                            <Text style={styles.title}>Add to list</Text>
                        </View>

                        {(<TouchableOpacity style={styles.plusButtonContainer} onPress={() => {
                            setNewListModal(true)
                        }}>
                            <AntDesign name={"plus"} size={35} color={"white"}/>
                        </TouchableOpacity>)}
                    </View>

                    {/*MODAL FOR NEW LIST*/}
                    <NewListModal modalNewList={newListModal} setModalNewList={setNewListModal}/>

                    {/*SEARCH BAR*/}
                    <SmallerSearchBar clicked={clicked} searchPhrase={searchPhrase} setSearchPhrase={setSearchPhrase}
                                      setClicked={setClicked}/>

                    {/*GRID COMPONENT*/}
                    <SquareComponentGrid title={""} data={newData} type={SquareComponentType.list}
                                         action={ListActions.add}/>
                </ScrollView>
            </SafeAreaView>
        </Modal>
    )
}

