 */
export default function EditListModal(props: EditListModalProps) {

    const userInt = useInteractions()

    const navigation = useNavigation()
    const [newNameList, setNewNameList] = useState("")
    const [confirmDelete, setConfirmDelete] = useState(false)

    const auth = useAuth()

    const showConfirmDialog = () => {
        return Alert.alert(
            "Are your sure?",
            "Are you sure you want to delete this list?",
            [
                // The "Yes" button
                {
                    text: "Yes",
                    onPress: () => {
                        userInt.deleteList(props.listId)
                        navigation.goBack()
                        auth.reloadData()
                        setConfirmDelete(false);

                    },
                },
                // The "No" button
                // Does nothing but dismiss the dialog when tapped
                {
                    text: "No",
                    onPress: () => {
                        setConfirmDelete(false)
                    }
                },
            ]
        );
    };

    //SWIPE DOWN TO DISMISS MODAL
    return (
        <GestureRecognizer onSwipeDown={() => props.setModalVisible(false)}>
            {/*MODAL */}
            <Modal animationType={"slide"} transparent={true} visible={props.modalVisible} onRequestClose={() => {
                props.setModalVisible(!props.modalVisible);
            }}>
                {/*MODAL VIEW*/}
                <View style={styles.containerModal}>
                    {/*CLOSE MODAL*/}
                    <TouchableOpacity style={styles.cross} onPress={() => props.setModalVisible(false)}>
                        <Entypo name={"cross"} size={25} color={"white"}/>
                    </TouchableOpacity>
                    {/*DELETE LIST BUTTON*/}
                    <TouchableOpacity style={styles.deleteList} onPress={() => setConfirmDelete(true)}>
                        <Octicons name={"trash"} size={25} color={"white"}/>
                    </TouchableOpacity>
                    {/*EDIT TITLE*/}
                    <Text style={styles.renameText}>Edit</Text>
                    {/*TEXT INPUT FOR RENAMING LIST*/}
                    <TextInput style={styles.textInput} placeholder={"Rename"} placeholderTextColor={"white"}
                               value={newNameList} onChangeText={setNewNameList}></TextInput>
                    {/*SUBMIT BUTTON*/}
                    <TouchableOpacity style={styles.submitButton}
                                      onPress={() => userInt.updateList(props.listId, newNameList)}>
                        <Text style={styles.submitText}>SUBMIT</Text>
                    </TouchableOpacity>
                    {/*CONFIRM MESSAGE IF DELETE*/}
                    {confirmDelete && showConfirmDialog()}
                </View>
            </Modal>
        </GestureRecognizer>
    )
}

import React, {useState} from 'react';
import {useAuth} from "../../../contexts/Auth";
import {URL} from "../../../services/constantes";
import Toast from "react-native-toast-message";
import * as ImagePicker from "expo-image-picker";
import {HeaderEditProfileComponentProps} from "../../../../types";
import {Modal} from "react-native";
import {ModalMatchingImageProps} from "../../../../types";
import {styles} from "../../Utilities/NewSearchBar/styles";
import {Entypo} from "@expo/vector-icons";


/**
 * Component displaying a header for {@link EditProfile}
 *
 * <img src="https://drive.google.com/uc?export=view&id=1CWGP8Gbqny20kHi_eQsAz77nok35_hZD" alt="ArtworkPage" height="500">

 * @param props : ModalMatchingImageProps
 */
export default function ModalMatchingImage(props: ModalMatchingImageProps) {

    const auth = useAuth()

    const [loading, setLoading] = useState(false)


    const data = {itemName: " ", artistName: " "}

    // API CALL TO POST NEW ICON IMAGE
    const sendDataProfile = (result: any) => {

        const xhr = new XMLHttpRequest();

        xhr.open('POST', URL + `images/submitImageForMatching?fileName=${data.itemName}&artist=${data.artistName}`);

        const formData = new FormData();

        formData.append('image', {
            // @ts-ignore
            uri: result.uri,
            type: result.type + "/" + result.uri.split("/")[11],
            name: result.uri.split("/")[11]
        });

        xhr.send(formData);
        console.log("STARTED")
        xhr.onreadystatechange = function () {
            if (xhr.status === 200) {
                Toast.show({
                    type: "dayl",
                    props: {
                        title: "Success",
                        text: "Matching received",
                    }
                })
                console.log(xhr)
                xhr.abort()
                return
            } else {
                Toast.show({
                    type: "dayl",
                    props: {
                        title: "Error",
                        text: "Matching not received",
                    }
                })
                console.log("DONE")
                console.log(xhr)

                xhr.abort()
                return
            }
        }

    }


    const pickImage = async () => {
        // CHOOSE PICTURE
        const result = await ImagePicker.launchImageLibraryAsync({
            mediaTypes: ImagePicker.MediaTypeOptions.Images,
            allowsEditing: true,
            quality: 0,
        });

        // CASE NO PICTURE WERE CHOSEN
        if (result.cancelled) {
            return
        }

        // CASE PICTURE IS VALID => SEND DATA TO API
        if (result) {
            sendDataProfile(result)
        }
    }

    return (

        <Entypo name={"camera"} size={25} style={styles.icon} onPress={() => pickImage()}/>

    )
}

