import {useInteractions} from "../../../contexts/UserInteractions";

// PROPS
export type ListGridProps = {
    title: string,
    data: List[],
    action: ListActions,
}

/**
 * Component used to display a button + grid of lists
 * @param {ListGridProps} props
 * @param {string} props.title - Title of the grid
 * @param {[List] | []} props.data - An array of {@link List}
 * @param {ListActions} [props.action] - Type of action
 * @constructor
 * @return ListGridComponent
 */
export default function ListGridComponent(props: ListGridProps) {

    const [modalNewList, setModalNewList] = useState(false)
    const [clicked, setClicked] = useState(false)
    const [searchPhrase, setSearchPhrase] = useState("")
    const [newData, setNewData] = useState<any>(props.data)

    const navigation = useNavigation()
    const userInt = useInteractions()

    useEffect(() => {
        setNewData(props.data)
    }, [])

    const onPressCreateNewList = () => {
        setModalNewList(true)
    }

    const onPressSubmitNewList = (listName: string) => {
        if (listName) {
            userInt.createNewList(listName);
            setModalNewList(false);
        } else {
            Toast.show({
                    type: "dayl",
                    props: {
                        title: "Failed",
                        text: "No name was given to the list !",
                    }
                }
            )
        }
    }

    useEffect(() => {
        const temp = props.data.filter(item => item.title.includes(searchPhrase))
        //@ts-ignore
        setNewData(temp)
    }, [searchPhrase])

    return (
        <SafeAreaView style={styles.container}>

            {/*NEW LIST MODAL*/}
            <NewListModal modalNewList={modalNewList} setModalNewList={setModalNewList}/>

            <View style={props.action === ListActions.detailed ? styles.DetailedHeaderListGrid : styles.HeaderListGrid}>
                {/*HEADER LISTS GRID*/}
                <TouchableOpacity onPress={() => navigation.navigate('AllMyListsPage')}>
                    <View style={styles.textAndChevron}>
                        {/*TEXT*/}
                        <Text
                            style={props.action === ListActions.detailed ? styles.detailedTitle : styles.title}>{props.title}</Text>
                        {/*CHEVRON*/}
                        {props.action === ListActions.view &&
                            <Entypo name={"chevron-right"} size={30} color={"white"}/>}

                    </View>
                </TouchableOpacity>

                {/* PLUS BUTTON*/}
                <View style={styles.plusButton}>
                    <TouchableOpacity style={styles.plusButton} onPress={() => onPressCreateNewList()}>
                        <AntDesign name={"plus"} size={props.action === ListActions.detailed ? 35 : 25}
                                   color={"white"}/>
                    </TouchableOpacity>
                </View>

            </View>
            {props.action === ListActions.detailed &&
                <SmallerSearchBar clicked={clicked} searchPhrase={searchPhrase} setSearchPhrase={setSearchPhrase}
                                  setClicked={setClicked}/>}

            <SquareComponentGrid title={props.title} data={searchPhrase === "" ? props.data : newData}
                                 type={SquareComponentType.list}
                                 action={props.action}/>
        </SafeAreaView>
    )
}

import React, {useEffect, useState} from 'react';
import axios from "axios";
import {URL} from "../../../services/constantes";
import {Artist} from "../../../../types";
import {useAuth} from "../../../contexts/Auth";
import ArtistGrid from "../ArtistGrid";
import {Text, View} from "react-native";
import styles from "../styles";
import {useNavigation} from "@react-navigation/native";

/**
 * Component used to display an {@link ArtistGrid} of the user's liked artists
 *
 * <img src="https://drive.google.com/uc?export=view&id=1Pgk0bWTRAdnPszMAHLiKggq3S_6T4fzJ" alt="ArtworkPage" height="500">
 * @constructor
 * @return ArtistGrid
 */
export default function MyLikedArtists() {

    const [myLikedArtists, setMyLikedArtists] = useState<[Artist] | []>([]);
    const auth = useAuth()
    const navigation = useNavigation()

    //API call (list of similar artist)
    useEffect(() => {
        setMyLikedArtists([])
        if (auth.authData.artistLiked) {
            auth.authData.artistLiked.forEach(artist => {
                axios.get(URL + `artist/${artist}`).then((response) => {
                    axios.get(URL + `paintingEntity/byArtist/${response.data.artistIdtfIntern}/0`).then((response2) => {

                        // PARSING DATA
                        if (response2.data[0].urlImage) {
                            //@ts-ignore
                            setMyLikedArtists(myLikedArtists => [...myLikedArtists, {
                                id: response.data.artistIdtfIntern,
                                title: response.data.artist_name_tot,
                                imageUri: URL + `images/content/mini/${response.data.artistIdtfIntern}/${response2.data[0].urlImage}`
                            }].slice(0, 4))
                        } else {
                            //@ts-ignore
                            setMyLikedArtists(myLikedArtists => [...myLikedArtists, {
                                id: response.data.artistIdtfIntern,
                                title: response.data.artist_name_tot,
                                imageUri: URL + `images/content/mini/${response.data.artistIdtfIntern}/${response2.data[1].urlImage}`
                            }].slice(0, 4))
                        }
                    })
                });
            })
        }
    }, [auth.authData.artistLiked]);

    //RETURN
    return !auth.authData.artistLiked || auth.authData.artistLiked.length === 0  ? <View><Text style={styles.noLikedArtists}>No liked artists </Text></View> : (
        <ArtistGrid title={"My liked artists"} data={myLikedArtists} navigate={() => navigation.navigate("AllMyArtistsPage")}/>
    )

}

