import React, {useEffect, useState} from 'react';
import axios from "axios";
import {URL} from "../../../services/constantes";
import {Artist} from "../../../../types";
import ArtistGrid from "../ArtistGrid";

/**
 * Component used to display a grid of 4 random artists
 *
 * <img src="https://drive.google.com/uc?export=view&id=1X0y_I7F3dmmADJcxTdgsCpijOvuY7dRt" alt="ArtworkPage" height="500">
 * @constructor
 * @return ArtistGrid
 */
export default function RandomArtists() {

    const [randomArtists, setRandomArtists] = useState<[Artist] | []>([]);

    //API call (list of random artist)
    useEffect(() => {
        axios.get(URL + `paintingFront/randomNArtistsAndMPaintings`).then((response) => {

            // PARSING DATA
            const data = response.data.map((el: any) => {
                return {
                    id: el.second[0].artistId,
                    imageUri: URL + `images/content/mini/${el.second[0].artistId}/${el.second[0].urlImage}`,
                    title: el.second[0].artisteName,
                }
            })

            setRandomArtists(data)
        });
    }, []);

    return (
        <ArtistGrid title={"Random artists"} data={randomArtists}/>
    )
}

import React, {useEffect, useState} from 'react';
import axios from "axios";
import {URL} from "../../../services/constantes";
import {Artist} from "../../../../types";
import ArtistGrid from "../ArtistGrid";

// PROPS
export type SimilarArtistsProps = {
    artistId: string,
}

/**
 * Component used to display a grid of 4 similar artists
 *
 * <img src="https://drive.google.com/uc?export=view&id=1tqlaBZP2Zc17YwnecW01Ftg8_7Igb26W" alt="ArtworkPage" height="500">
 * @param {SimilarArtistsProps} props
 * @param {string} props.artistId - ID of the artist
 * @constructor
 * @return ArtistGrid
 * */
export default function SimilarArtists(props: SimilarArtistsProps) {

    const [listSimilarArtist, setListSimilarArtist] = useState<[Artist] | []>([]);

    //API CALL (list of similar artist)
    useEffect(() => {

        axios.get(URL + `similarArtists/findByArtistId/${props.artistId}/?numberOfArtists=49`).then((response) => {

            // PARSING DATA
            let data = response.data.filter(function (el: any) {
                return el.artistDisplayDTO != null;
            }).map(function (el: any) {
                return {
                    id: el.artistDisplayDTO.functionalId,
                    imageUri: URL + `images/content/mini/${el.artistDisplayDTO.functionalId}/${el.artistDisplayDTO.backgroundImage}`,
                    title: el.artistDisplayDTO.name
                }
            })
            const fourArtist = data.slice(1, 5)

            setListSimilarArtist(fourArtist)
        });
    }, [props.artistId]);

    return (
        <ArtistGrid title={"Similar artists"} data={listSimilarArtist}/>
    )
}

import React from 'react';
import {SafeAreaView, View} from 'react-native';
import {Artist, List, ListActions, SquareComponentType} from "../../../types";
import styles from "./styles"
import ListComponent from "../SquareComponent/ListComponent";
import ArtistComponent from "../SquareComponent/ArtistComponent";

// PROPS
export type SquareComponentGridProps = {
    title: string,
    data: Artist[] | List[] | [],
    type: SquareComponentType,
    action?: ListActions,
}

/**
 * Component used to display a square component according to its {@link SquareComponentType}
 * @param {SquareComponentGridProps} props
 * @param {string} props.title - Title of the grid
 * @param {Artist[] | List[] | []} props.data - Array of {@link Artist} or {@link List}
 * @param {SquareComponentType} props.type - Type of {@link SquareComponentType}
 * @param {ListActions} [props.action] - Type of action
 * @constructor
 * @return SquareComponentGrid
 */
export default function SquareComponentGrid(props: SquareComponentGridProps) {

    return (
        <SafeAreaView style={styles.container}>
            <View style={styles.grid}>
                {props.type === SquareComponentType.list ? props.data.map((item) => {
                    //@ts-ignore
                    return (<ListComponent id={item.id} title={item.title} fourImages={item.fourImages} action={props.action} key={item.id}/>)
                }) : props.data.map((item) => {
                    //@ts-ignore
                    return <ArtistComponent id={item.id} title={item.title} imageUri={item.imageUri} key={item.id}/>
                })}
            </View>
        </SafeAreaView>
    )
}

import React, {useEffect, useState} from 'react';
import {Text, TouchableOpacity, View} from "react-native";
import styles from "./styles";
import axios from "axios";
import {useAuth} from "../../contexts/Auth";
import {URL} from "../../services/constantes";
import {Stats} from "../../../types";
import {useNavigation} from "@react-navigation/native";

/**
 * Component displaying the user's data (FOLLOWING/FOLLOWERS/CONNECTIONS) in {@link MyProfile}
 *
 * <img src="https://drive.google.com/uc?export=view&id=1YGroO4InY45ho6io6yUwwjcPTKjMZPE2" alt="ArtworkPage" height="100">
 * @constructor
 * @return UserData
 */
export default function UserData() {

    const auth = useAuth()
    const navigation = useNavigation()
    const [stats, setStats] = useState<Stats>({numberFollowers: 0, numberFollowing: 0, numberOfDrafts: 0});

    // API CALL TO GET THE STATS OF THE USER
    useEffect(() => {
        axios
            .get(URL + `users/statistics?userId=${auth.authData.id}`
            )
            .then((response) => {
                setStats(response.data)
            });

    }, [auth.authData.id]);

    return (
        <View style={styles.container}>
            {/*USER FOLLOWING*/}
            <TouchableOpacity style={styles.dataContainer} onPress={() => navigation.navigate("MyFollowedPage")}>
                <Text style={styles.stats}>{stats.numberFollowing}</Text>
                <Text style={styles.subtitle}>Following</Text>
            </TouchableOpacity>
            {/*USER FOLLOWERS*/}
            <View style={styles.dataContainer}>
                <Text style={styles.stats}>{stats.numberFollowers}</Text>
                <Text style={styles.subtitle}>Followers</Text>
            </View>
            {/*USER CONNECTIONS*/}
            <View style={styles.dataContainer}>
                <Text style={styles.stats}>0</Text>
                <Text style={styles.subtitle}>Connections</Text>
            </View>
        </View>
    )
}

