import React, {useEffect, useState} from "react";
import {Text, TextInput, View} from "react-native";
import {Entypo} from "@expo/vector-icons";
import {styles} from "./styles";

// PROPS
export type BiographyInputProps = {
    credentialsNeeded: string,
    value: string,
    setValue : any,
}

/**
 * Component displaying an input field for the user to insert his bio in {@link EditProfile} page
 *
 * <img src="https://drive.google.com/uc?export=view&id=1PEq6_DIXaVY55LWLF3UDYvXtOmNp6_uH" alt="ArtworkPage" height="100">
 * @param {BiographyInputProps} props
 * @param {string} props.credentialsNeeded - Title of the credential field
 * @param {string} props.value - Value of the text input
 * @param {any} props.setValue - Function used to set the value
 * @constructor
 * @return BiographyInput
 */
export default function BiographyInput(props: BiographyInputProps) {

    const [clicked, setClicked] = useState(false)
    const [numberOfChar, setNumberOfChar] = useState(props.value.length)

    useEffect(() => {
        setNumberOfChar(props.value.length)
    }, [props.value])

    return (
        <View style={styles.container}>
            <Text style={styles.text}>{props.credentialsNeeded}</Text>
            <View style={styles.searchBar__unclicked}>

                {/* Input field */}
                <TextInput
                    onEndEditing={() => setClicked(false)}
                    style={styles.input}
                    value={props.value}
                    onChangeText={props.setValue}
                    maxLength={250}
                    multiline={true}

                    onFocus={() => {
                        setClicked(true);
                    }}
                />

                <View style={styles.rightContainer}>
                    {/* cross Icon, depending on whether the search bar is clicked or not */}
                    {clicked && (<Entypo name="cross" size={20} color="black" style={{marginRight: 20}} onPress={() => {
                        props.setValue("")
                    }}/>)}
                </View>

            </View>

            {/*NUMBER OF CHARS LEFT FOR THE BIO*/}
            <Text style={{
                color: "white",
                textAlign: "right",
                flex: 1,
                alignSelf: "flex-end",
                marginTop: 10,
                marginRight: 20
            }}>{numberOfChar}/250</Text>
        </View>
    )
}

import React, {useState} from "react";
import {Text, TextInput, View} from "react-native";
import {AntDesign, Entypo} from "@expo/vector-icons";
import {styles} from "./styles";

// PROPS
export type CredentialsInputProps = {
    credentialsNeeded: string,
    value: string,
    setValue : any,
}

/**
 * Component displaying an input field for the user to insert his credentials in {@link Login} and {@link SignUp} pages
 *
 * <img src="https://drive.google.com/uc?export=view&id=1IO8nhdGFahbBJki-0lGEoZY8lRuYdWDP" alt="ArtworkPage" height="100">
 * @param {CredentialsInputProps} props
 * @param {string} props.credentialsNeeded - Title of the credential field
 * @param {string} props.value - Value of the text input
 * @param {any} props.setValue - Function used to set the value
 * @constructor
 * @return CredentialsInputComponent
 */
export default function CredentialsInputComponent(props: CredentialsInputProps) {

    const [clicked, setClicked] = useState(false)
    const [hideField, setHideField] = useState(props.credentialsNeeded === "Password" || props.credentialsNeeded === "Confirm password")

    return (
        <View style={styles.container}>
            {/*TITLE*/}
            <Text style={styles.text}>{props.credentialsNeeded}</Text>

            <View style={styles.searchBar__unclicked}>
                {/* Input field */}
                <TextInput
                    onEndEditing={() => setClicked(false)}
                    secureTextEntry={hideField}
                    style={styles.input}
                    value={props.value}
                    onChangeText={props.setValue}
                    onFocus={() => {
                        setClicked(true);
                    }}
                />
                <View style={styles.rightContainer}>
                    {/* cross Icon, depending on whether the search bar is clicked or not */}
                    {clicked && (<Entypo name="cross" size={20} color="black" style={{marginRight: 20}} onPress={() => {
                        props.setValue("")
                    }}/>)}
                    {(props.credentialsNeeded === "Password" || props.credentialsNeeded === "Confirm password") &&
                        <AntDesign name={"eye"} size={20} onPress={() => setHideField(!hideField)}/>}
                </View>
            </View>
        </View>
    )
}

