/**
 * Component displaying a modal for showing an image in fullscreen
 *
 * <img src="https://drive.google.com/uc?export=view&id=10QiizGTQZOLSibKi2GUtNz7XKO7K0qf-" alt="ArtworkPage" height="500">
 * @param {onClickImageProps} props
 * @param {string} props.imageUri - URI of the image
 * @param {string} props.title - Title of the image
 * @param {boolean} props.onClickImage - Visibility of the modal
 * @param {void} props.setOnClickImage - Toggles the visibility of the modal
 * @param {string} [props.date] - Date of the artwork
 * @param {string} [props.artistName] - Name of the artist
 * @constructor
 * @return Modal
 */
export default function OnClickImageComponent(props: onClickImageProps) {

    const renderFooter = () => {
        return (
            <View style={styles.infoContainer}>
                <Text style={styles.title}>{props.title}</Text>
                {props.artistName && <Text style={styles.subtitle}>{props.artistName}</Text>}
                {props.date && <Text style={styles.date}>{props.date}</Text>}
            </View>
        )
    }

    return (
        <Modal animationIn={"fadeIn"} style={styles.modal} backdropOpacity={0} isVisible={props.onClickImage}
               onBackButtonPress={() => {
                   props.setOnClickImage(!props.onClickImage)
               }} statusBarTranslucent={true}>
            <StatusBar backgroundColor={"black"} showHideTransition={"none"}/>

            {/*IMAGE VIEWER*/}
            <ImageViewer enableSwipeDown={true} onSwipeDown={() => props.setOnClickImage(false)} swipeDownThreshold={20}
                         style={styles.zoomableView} imageUrls={[{url: props.imageUri.replace('/mini', '')}]}
                         renderIndicator={() => (<View></View>)} footerContainerStyle={styles.infoContainer}
                         renderFooter={renderFooter}/>

            {/*BACK BUTTON*/}
            <TouchableOpacity style={styles.closeButton} onPress={() => props.setOnClickImage(!props.onClickImage)}>
                <Entypo name={"cross"} size={30} color={"white"}/>
            </TouchableOpacity>
        </Modal>
    )
}

