// USED FOR REFRESHING
const wait = (timeout: number) => {
    return new Promise(resolve => setTimeout(resolve, timeout));
}

/**
 * Profile page of the user
 *
 * <img src="https://drive.google.com/uc?export=view&id=1WgHBpa1CEHrexFU6I9vf5w_QyG8-DWAL" alt="ArtworkPage" height="500">
 * @constructor
 * @return MyProfile
 */
export default function MyProfile() {

    const scrollY = useRef(new Animated.Value(0)).current;
    const auth = useAuth()

    const [threeDotsVisible, setThreeDotsVisible] = useState(false)

    // USE STATE FOR REFRESHING PAGE
    const [refreshPage, setRefreshPage] = useState(false)

    // CALLBACK TO REFRESH PAGE WHEN PULLING UP
    const onRefresh = React.useCallback(() => {
        auth.reloadData()
        setRefreshPage(true);
        wait(2000).then(() => setRefreshPage(false));
    }, []);

    // FUNCTION TO MONITOR SCROLL Y AND DEACTIVATE TOP BAR ACCORDINGLY
    function deactivateTopBar(event: any) {
        if (event.nativeEvent.contentOffset.y > 90)
            setPointerEvenTopBar("auto")
        else
            setPointerEvenTopBar("none")
    }

    //USE STATE TO DEACTIVATE TOP BAR WHEN SCROLL UP
    let [pointerEventTopBar, setPointerEvenTopBar] = useState<any>("none")

    const threeMenuOpacity = scrollY.interpolate({
        inputRange: [-20, -10, 0], outputRange: [0, 0.4, 1], extrapolate: 'clamp',
    })

    return (
        <View>
            {/*ANIMATED TOP BAR THAT APPEARS WHEN SCROLL DOWN*/}
            <View style={[styles.topBar]} pointerEvents={pointerEventTopBar}>
                <TopBarAnimatedHeader title={auth.authData.firstName + " " + auth.authData.lastName} type={HeaderType.User} scrollY={scrollY}/>
            </View>

            {/*USER MENU BUTTON*/}
            <TouchableOpacity style={[styles.menuButton]} onPress={() => setThreeDotsVisible(!threeDotsVisible)}>
                <Animated.View style={{opacity: threeMenuOpacity}}>
                    <Entypo name={"menu"} size={30} color={"white"}/>
                </Animated.View>
            </TouchableOpacity>

            {/*USER MENU MODAL*/}
            <ThreeDotsProfileModal modalVisible={threeDotsVisible} setModalVisible={setThreeDotsVisible}/>


            {/*ANIMATED SCROLL VIEW*/}
            <Animated.ScrollView alwaysBounceVertical={true} scrollEventThrottle={16}
                                 showsVerticalScrollIndicator={false}
                                 refreshControl={<RefreshControl refreshing={refreshPage} onRefresh={onRefresh}/>}
                                 onScroll={Animated.event([{nativeEvent: {contentOffset: {y: scrollY}}}], {
                                     useNativeDriver: true,
                                     listener: (event: Object) => deactivateTopBar(event)
                                 })}>

                {/*HEADER USER COMPONENT*/}
                <HeaderUserComponent scrollY={scrollY}/>

                {/*USER DATA*/}
                <UserData/>

                {/*BIOGRAPHY USER*/}
                <Informations title={"Profile"} text={auth.authData.biography}/>

                {/*LIKED WORKS*/}
                <LikedArtworksCarousel/>

                {/*LISTS*/}
                <MyListsComponent action={ListActions.view}/>

                {/*MY ARTISTS*/}
                <MyLikedArtists/>




            </Animated.ScrollView>
        </View>)
}

